import React, {useState, useEffect} from 'react';
import './App.css';

function App() {
  const [count, setCount ] = useState(1);

  const handleCountUpdate = () =>{
    setCount(count + 1);
  };

  useEffect(() => {
      console.log('렌더링');
    }
  )

  return (
    <div className="App">
        <button onClick={handleCountUpdate}>update</button>
        <p>{count}</p>
    </div>
  );
}

export default App;
